import React, { useRef, useState } from "react";

// Target-style returns UI (single-file React component)
// Default export a React component so it can be dropped into a React app (src/App.jsx)

export default function TargetReturnUI() {
  const [stage, setStage] = useState("home"); // home | form
  const [imageFile, setImageFile] = useState(null);
  const [imageDataUrl, setImageDataUrl] = useState(null);
  const [reason, setReason] = useState("");
  const [processing, setProcessing] = useState(false);
  const [aiResult, setAiResult] = useState(null); // { eligible: boolean, reasons: string[] }
  const [notes, setNotes] = useState("");

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [usingCamera, setUsingCamera] = useState(false);

  // Handle file upload (desktop or mobile gallery)
  const handleFileChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    setImageFile(file);
    const reader = new FileReader();
    reader.onload = (ev) => setImageDataUrl(ev.target.result);
    reader.readAsDataURL(file);
    setAiResult(null);
    setNotes("");
  };

  // Start camera (for desktop/mobile that allow camera access)
  const startCamera = async () => {
    setUsingCamera(true);
    setAiResult(null);
    setNotes("");
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { facingMode: "environment" } });
      if (videoRef.current) videoRef.current.srcObject = stream;
    } catch (err) {
      console.error("camera error", err);
      setNotes("Camera access blocked or not available on this device.");
      setUsingCamera(false);
    }
  };

  // Take snapshot from camera video
  const captureFromCamera = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0);
    const dataUrl = canvas.toDataURL("image/jpeg", 0.9);
    setImageDataUrl(dataUrl);
    setUsingCamera(false);

    // Stop the tracks
    const tracks = video.srcObject?.getTracks?.();
    tracks?.forEach((t) => t.stop());
    video.srcObject = null;
    setAiResult(null);
    setNotes("");
  };

  // Simulate calling AI backend (UI only) — this is purely frontend placeholder
  const processImageWithAi = () => {
    if (!imageDataUrl) {
      setNotes("Please upload or capture an image before processing.");
      return;
    }

    setProcessing(true);
    setAiResult(null);
    setNotes("");

    // NOTE: this timeout only simulates a real AI call; in production you would POST the file
    setTimeout(() => {
      // naive heuristics for a demo: look at reason and filename length to decide outcome
      const lowerReason = reason.toLowerCase();
      const reasons = [];
      let eligible = true;

      if (!reason || reason.trim().length < 5) {
        reasons.push("Return reason is too short — please provide more detail.");
        eligible = false;
      }

      // faux-blurriness detection by checking imageDataUrl length (not real)
      if (imageDataUrl.length < 10000) {
        reasons.push("Image looks low-resolution or blurry.");
        eligible = false;
      }

      // add failure example for "wrong item" words
      if (lowerReason.includes("wrong item") || lowerReason.includes("not what i ordered") || lowerReason.includes("different")) {
        reasons.push("Product mismatch noted: please include order number or a picture of packing label.");
      }

      if (reasons.length === 0) reasons.push("No obvious issues detected. We can proceed with the return.");

      setAiResult({ eligible, reasons });
      setProcessing(false);
    }, 800); // quick simulated response for UI demo
  };

  const resetForm = () => {
    setImageFile(null);
    setImageDataUrl(null);
    setReason("");
    setAiResult(null);
    setNotes("");
    setUsingCamera(false);
  };

  // Small helper UI blocks
  const ResultBadge = ({ eligible }) => (
    <div className={`inline-flex items-center gap-2 px-3 py-1 rounded-full text-sm font-medium ${eligible ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
      <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden>
        {eligible ? (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
        ) : (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
        )}
      </svg>
      {eligible ? 'Eligible for return' : 'Not eligible'}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900 p-6 font-sans">
      <header className="max-w-4xl mx-auto mb-6 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="h-10 w-10 rounded-full bg-red-600 flex items-center justify-center text-white font-bold">T</div>
          <div>
            <h1 className="text-xl font-semibold">Returns — Damaged or Bad Product</h1>
            <p className="text-sm text-gray-500">Quickly check eligibility by uploading a photo of the item.</p>
          </div>
        </div>
        <div>
          <button
            onClick={() => { setStage('form'); resetForm(); }}
            className="inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-red-600 text-white shadow hover:bg-red-700"
          >
            Start a return
          </button>
        </div>
      </header>

      {stage === 'home' && (
        <main className="max-w-4xl mx-auto bg-white rounded-2xl p-6 shadow-sm">
          <h2 className="text-lg font-medium mb-2">Why use this flow?</h2>
          <ul className="list-disc ml-5 text-sm text-gray-600 space-y-1 mb-4">
            <li>Upload or capture a photo of the damaged/bad product.</li>
            <li>Provide a reason for return and any supporting notes.</li>
            <li>AI validator will analyse the image and give immediate UI feedback (demo).</li>
          </ul>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-2 p-4 border rounded-lg">
              <h3 className="font-semibold mb-2">Start here</h3>
              <p className="text-sm text-gray-600">Click "Start a return" to open the returns form. This demo is front-end-only and simulates the AI results.</p>
            </div>

            <div className="p-4 border rounded-lg">
              <h3 className="font-semibold mb-2">Helpful tips</h3>
              <ol className="text-sm text-gray-600 list-decimal ml-5 space-y-1">
                <li>Show the damaged area clearly.</li>
                <li>Include packaging or label if mismatch.</li>
                <li>Provide concise reason to speed approval.</li>
              </ol>
            </div>
          </div>
        </main>
      )}

      {stage === 'form' && (
        <main className="max-w-4xl mx-auto bg-white rounded-2xl p-6 shadow-sm space-y-6">
          <div className="flex items-start gap-6">
            <div className="w-2/3">
              <label className="block text-sm font-medium mb-2">Upload or capture image</label>

              <div className="border border-dashed rounded-lg p-4 bg-gray-50">
                <div className="flex gap-3 items-center mb-3">
                  <label className="cursor-pointer inline-flex items-center gap-2 px-3 py-2 rounded-lg bg-white shadow text-sm">
                    <input accept="image/*" type="file" onChange={handleFileChange} className="hidden" />
                    Upload from device
                  </label>

                  <button
                    onClick={startCamera}
                    className="inline-flex items-center gap-2 px-3 py-2 rounded-lg bg-red-600 text-white text-sm shadow"
                  >
                    Use camera
                  </button>

                  <button
                    onClick={() => { setImageDataUrl(null); setImageFile(null); setAiResult(null); setNotes(""); }}
                    className="inline-flex items-center gap-2 px-3 py-2 rounded-lg bg-white text-sm border"
                  >
                    Clear
                  </button>
                </div>

                {usingCamera && (
                  <div className="space-y-2">
                    <video ref={videoRef} autoPlay playsInline className="w-full h-64 object-cover rounded-md border" />
                    <div className="flex gap-2">
                      <button onClick={captureFromCamera} className="px-3 py-2 bg-red-600 text-white rounded-lg">Capture</button>
                      <button onClick={() => {
                        const tracks = videoRef.current?.srcObject?.getTracks?.();
                        tracks?.forEach((t) => t.stop());
                        if (videoRef.current) videoRef.current.srcObject = null;
                        setUsingCamera(false);
                      }} className="px-3 py-2 bg-white border rounded-lg">Cancel</button>
                    </div>
                  </div>
                )}

                {imageDataUrl ? (
                  <div className="mt-3 grid grid-cols-1 md:grid-cols-2 gap-3">
                    <div>
                      <img src={imageDataUrl} alt="preview" className="w-full max-h-64 object-contain rounded-md border" />
                    </div>
                    <div className="p-3 bg-white rounded-md border">
                      <p className="text-sm text-gray-600">Preview and basic checks</p>
                      <ul className="text-sm mt-2 text-gray-700 list-disc ml-5">
                        <li>File size: approx {Math.round((imageDataUrl.length * 3) / 4 / 1024)} KB (simulated)</li>
                        <li>Resolution: {imageDataUrl.includes('data:image') ? 'detected' : 'unknown'}</li>
                        <li>Format: JPEG/PNG (client-side)</li>
                      </ul>
                    </div>
                  </div>
                ) : (
                  <div className="mt-3 text-sm text-gray-500">No image selected. Use upload or camera to attach a photo of the product.</div>
                )}

                <canvas ref={canvasRef} style={{ display: 'none' }} />

              </div>

              <div className="mt-4">
                <label className="block text-sm font-medium mb-2">Reason for return</label>
                <textarea value={reason} onChange={(e) => setReason(e.target.value)} rows={4} className="w-full rounded-lg border p-3 text-sm" placeholder="Explain what is wrong with the product, e.g. broken on arrival, missing parts, wrong item, severely damaged packaging..." />
              </div>

              <div className="mt-4 flex items-center gap-3">
                <button disabled={processing} onClick={processImageWithAi} className="px-4 py-2 rounded-lg bg-red-600 text-white shadow hover:bg-red-700">Process image</button>
                <button onClick={() => { setStage('home'); resetForm(); }} className="px-4 py-2 rounded-lg bg-white border">Back</button>
                <div className="ml-auto text-sm text-gray-500">Status: {processing ? 'analysing image...' : (aiResult ? (aiResult.eligible ? 'Ready' : 'Issues found') : 'idle')}</div>
              </div>

            </div>

            <aside className="w-1/3 p-4 border rounded-lg bg-gray-50">
              <h3 className="font-semibold mb-2">Quick summary</h3>
              <p className="text-sm text-gray-600 mb-3">This panel shows the AI validator output and guidance for the next steps.</p>

              {aiResult ? (
                <div className="space-y-3">
                  <div>
                    <ResultBadge eligible={aiResult.eligible} />
                  </div>

                  <div className="bg-white p-3 rounded-md border text-sm">
                    <p className="font-medium mb-1">Why</p>
                    <ul className="list-disc ml-5 text-gray-700 text-sm">
                      {aiResult.reasons.map((r, i) => <li key={i}>{r}</li>)}
                    </ul>
                  </div>

                  <div className="text-sm">
                    <p className="font-medium mb-1">Suggested actions</p>
                    <ol className="list-decimal ml-5 text-gray-700">
                      {!aiResult.eligible && <li>Retake a clearer photo (show damaged area closely).</li>}
                      {aiResult.eligible && <li>Proceed to create a return label in the next step (not in this demo).</li>}
                      <li>Include order number and packing label if mismatch reported.</li>
                    </ol>
                  </div>
                </div>
              ) : (
                <div className="text-sm text-gray-500">No results yet. Click <b>Process image</b> after uploading a photo and entering a reason.</div>
              )}

              {notes && <div className="mt-3 text-xs text-yellow-800 bg-yellow-50 p-2 rounded">Note: {notes}</div>}

            </aside>
          </div>

          <section className="border-t pt-4">
            <h3 className="font-semibold mb-2">Image processing log</h3>
            <div className="bg-gray-100 p-3 rounded text-sm">
              <p className="mb-2">AI validator output will appear here after processing. This demo shows simulated output and suggested next steps.</p>
              <div className="text-xs text-gray-600">Timestamp: {new Date().toLocaleString()}</div>
            </div>
          </section>

          <footer className="flex items-center justify-between">
            <div className="text-sm text-gray-600">Need help? Contact Target Support or visit our returns policy page.</div>
            <div className="flex gap-2">
              <button onClick={() => { navigator.clipboard?.writeText(window.location.href); }} className="px-3 py-2 border rounded bg-white">Copy Link</button>
              <button onClick={() => { resetForm(); }} className="px-3 py-2 bg-white border rounded">Reset</button>
            </div>
          </footer>

        </main>
      )}

    </div>
  );
}
