import React, { useEffect, useRef, useState } from "react";
import "./index.css";

export default function App() {
  const [stage, setStage] = useState("home");
  const [imageFile, setImageFile] = useState(null);
  const [imageDataUrl, setImageDataUrl] = useState(null);
  const [reason, setReason] = useState("");
  const [processing, setProcessing] = useState(false);
  const [aiResult, setAiResult] = useState(null);
  const [notes, setNotes] = useState("");

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [usingCamera, setUsingCamera] = useState(false);

  // --- iPhone specific new states ---
  const isIOS = /iPhone|iPad|iPod/i.test(navigator.userAgent);
  const [cameraActive, setCameraActive] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [showPhotoPreview, setShowPhotoPreview] = useState(false);

  // ----------------------------
  // CLEANUP CAMERA ON UNMOUNT
  // ----------------------------
  useEffect(() => {
    return () => {
      try {
        const video = videoRef.current;
        const tracks = video?.srcObject?.getTracks?.();
        tracks?.forEach((t) => t.stop());
      } catch (e) {}
    };
  }, []);

  // ----------------------------
  // FILE UPLOAD HANDLER
  // ----------------------------
  const handleFileChange = (e) => {
    const file = e.target.files?.[0] ?? null;
    if (!file) return;

    setImageFile(file);
    const reader = new FileReader();
    reader.onload = (ev) => setImageDataUrl(String(ev.target.result));
    reader.readAsDataURL(file);

    setAiResult(null);
    setNotes("");
  };

  // ----------------------------
  // IOS-SAFE CAMERA START
  // ----------------------------
  const startCamera = async () => {
    try {
      const constraints = {
        video: {
          facingMode: "environment",
        },
        audio: false,
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.setAttribute("playsInline", "true"); // iOS fix
        await videoRef.current.play();
      }

      setCameraActive(true);
      setUsingCamera(true);
    } catch (err) {
      console.error("Camera error:", err);
      alert("Camera access blocked. Please use file upload instead.");
    }
  };

  // ----------------------------
  // iPHONE FALLBACK FILE UPLOAD
  // ----------------------------
  const handleFileUploadIOS = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const imgURL = URL.createObjectURL(file);
    setCapturedImage(imgURL);
    setImageDataUrl(imgURL);
    setShowPhotoPreview(true);

    setAiResult(null);
    setNotes("");
  };

  // ----------------------------
  // CAPTURE PHOTO (NORMAL & iOS)
  // ----------------------------
  const captureFromCamera = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (!video || !canvas) return;

    canvas.width = video.videoWidth || 1280;
    canvas.height = video.videoHeight || 720;

    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    const dataUrl = canvas.toDataURL("image/jpeg", 0.92);

    setImageDataUrl(dataUrl);
    setCapturedImage(dataUrl);
    setCameraActive(false);
    setUsingCamera(false);

    const tracks = video.srcObject?.getTracks?.();
    tracks?.forEach((t) => t.stop());
    video.srcObject = null;

    setAiResult(null);
    setNotes("");
  };

  // ----------------------------
  // PROCESS IMAGE (FAKE AI)
  // ----------------------------
  const processImageWithAi = () => {
    if (!imageDataUrl) {
      setNotes("Please upload or capture an image before processing.");
      return;
    }

    setProcessing(true);
    setAiResult(null);
    setNotes("");

    setTimeout(() => {
      const lowerReason = String(reason).toLowerCase();
      const reasons = [];
      let eligible = true;

      if (!reason || reason.trim().length < 5) {
        reasons.push("Return reason is too short please provide more details.");
        eligible = false;
      }

      if (String(imageDataUrl).length < 10000) {
        reasons.push("Image looks low-resolution or blurry.");
        eligible = false;
      }

      if (
        lowerReason.includes("wrong item") ||
        lowerReason.includes("not what i ordered") ||
        lowerReason.includes("different")
      ) {
        reasons.push("Product mismatch noted: include order number or a picture of packing label.");
      }

      if (reasons.length === 0) {
        reasons.push("No obvious issues detected. We can proceed with the return.");
      }

      setAiResult({ eligible, reasons });
      setProcessing(false);
    }, 900);
  };

  // ----------------------------
  // RESET FORM
  // ----------------------------
  const resetForm = () => {
    try {
      const video = videoRef.current;
      const tracks = video?.srcObject?.getTracks?.();
      tracks?.forEach((t) => t.stop());
      if (video) video.srcObject = null;
    } catch (e) {}

    setImageFile(null);
    setImageDataUrl(null);
    setCapturedImage(null);
    setShowPhotoPreview(false);
    setCameraActive(false);
    setUsingCamera(false);
    setReason("");
    setAiResult(null);
    setNotes("");
    setProcessing(false);
  };

  // ----------------------------
  // RESULT BADGE
  // ----------------------------
  const ResultBadge = ({ eligible }) => (
    <div className={`badge ${eligible ? "badge-yes" : "badge-no"}`}>
      <svg viewBox="0 0 24 24" className="badge-icon" aria-hidden>
        {eligible ? (
          <path d="M5 13l4 4L19 7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" fill="none" />
        ) : (
          <path d="M6 18L18 6M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" fill="none" />
        )}
      </svg>
      <span>{eligible ? "Eligible for return" : "Not eligible"}</span>
    </div>
  );

  // ============================
  // RETURN (UI)
  // ============================
  return (
    <div className="app-root">
      {/* NAVBAR */}
      <header className="nav">
        <div className="nav-inner">
          <div className="brand">
            <div className="bullseye" aria-hidden>
              <svg viewBox="0 0 100 100" className="bull-svg">
                <circle cx="50" cy="50" r="50" fill="#fff" />
                <circle cx="50" cy="50" r="38" fill="#cc0000" />
                <circle cx="50" cy="50" r="22" fill="#fff" />
                <circle cx="50" cy="50" r="10" fill="#cc0000" />
              </svg>
            </div>
            <div className="brand-text">
              <div className="brand-name">Target</div>
              <div className="brand-sub">Returns</div>
            </div>
          </div>

          <div className="nav-links">
            <button className="nav-link" onClick={() => { setStage("home"); resetForm(); }}>Home</button>
            <button className="nav-link" onClick={() => alert("Orders page (demo)")}>Orders</button>
            <button className="nav-cta" onClick={() => { setStage("form"); resetForm(); }}>Start return</button>
          </div>
        </div>
      </header>

      {/* HERO */}
      <section className="hero">
        <div className="hero-inner">
          <h1 className="hero-title">Return Damaged or Bad Product</h1>
          <p className="hero-sub">Quickly check eligibility by uploading a photo of the item.</p>
          <button className="btn btn-white" onClick={() => { setStage("form"); resetForm(); }}>Start return</button>
        </div>
      </section>

      {/* MAIN SECTION */}
      <main className="main-wrap">
        {stage === "form" && (
          <section className="center-card">
            <div className="form-grid">

              {/* LEFT PANEL */}
              <div className="form-left">
                <label className="label">Upload or capture image</label>

                <div className="uploader">

                  <div className="uploader-actions">

                    {/* Normal upload */}
                    <label className="upload-btn">
                      <input accept="image/*" type="file" onChange={handleFileChange} />
                      Upload from device
                    </label>

                    {/* Normal camera (Android + Desktop) */}
                    {!isIOS && (
                      <button className="btn-outline-red" onClick={startCamera}>
                        Use camera
                      </button>
                    )}

                    {/* iPhone Camera Fallback */}
                    {isIOS && (
                      <input
                        type="file"
                        accept="image/*"
                        capture="environment"
                        onChange={handleFileUploadIOS}
                        className="ios-camera-input"
                      />
                    )}

                    <button
                      className="btn btn-white small"
                      onClick={() => {
                        setImageDataUrl(null);
                        setCapturedImage(null);
                        setAiResult(null);
                        setNotes("");
                      }}
                    >
                      Clear
                    </button>
                  </div>

                  {/* Live Camera View */}
                  {cameraActive && !isIOS && (
                    <div className="camera-area">
                      <video ref={videoRef} autoPlay playsInline className="video" />

                      <div className="camera-controls">
                        <button className="btn btn-red" onClick={captureFromCamera}>Capture</button>
                        <button
                          className="btn btn-white small"
                          onClick={() => {
                            const tracks = videoRef.current?.srcObject?.getTracks?.();
                            tracks?.forEach((t) => t.stop());
                            videoRef.current.srcObject = null;
                            setCameraActive(false);
                          }}
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  )}

                  {/* PREVIEW */}
                  {imageDataUrl ? (
                    <div className="preview-row">
                      <img src={imageDataUrl} className="preview-img" alt="preview" />
                      <div className="preview-meta">
                        <p className="muted">Preview and basic checks</p>
                        <ul>
                          <li>File size: approx {Math.round((String(imageDataUrl).length * 3) / 4 / 1024)} KB</li>
                          <li>Resolution: detected</li>
                          <li>Format: JPEG/PNG</li>
                        </ul>
                      </div>
                    </div>
                  ) : (
                    <div className="muted">No image selected yet.</div>
                  )}

                  <canvas ref={canvasRef} style={{ display: "none" }} />
                </div>

                {/* Reason input */}
                <div className="mt-16">
                  <label className="label">Reason for return</label>
                  <textarea
                    className="textarea"
                    value={reason}
                    onChange={(e) => setReason(e.target.value)}
                    placeholder="Explain what is wrong..."
                  />
                </div>

                {/* Buttons */}
                <div className="form-actions">
                  <button className="btn btn-red" disabled={processing} onClick={processImageWithAi}>
                    {processing ? "Analysing..." : "Process image"}
                  </button>

                  <button className="btn btn-white" onClick={() => { setStage("home"); resetForm(); }}>Back</button>

                  <div className="status">
                    {processing ? "analysing..." : aiResult ? (aiResult.eligible ? "Ready" : "Issues found") : "idle"}
                  </div>
                </div>
              </div>

              {/* RIGHT PANEL */}
              <aside className="form-right">
                <div className="card small">
                  <h4>Quick summary</h4>
                  <p className="muted">AI validator output will show here.</p>

                  {aiResult ? (
                    <>
                      <ResultBadge eligible={aiResult.eligible} />

                      <div className="why">
                        <p className="strong">Why</p>
                        <ul>
                          {aiResult.reasons.map((r, i) => <li key={i}>{r}</li>)}
                        </ul>
                      </div>
                    </>
                  ) : (
                    <p className="muted small">No results yet.</p>
                  )}

                  {notes && <div className="note">Note: {notes}</div>}
                </div>
              </aside>
            </div>

            {/* LOG */}
            <div className="log mt-18">
              <h4>Image processing log</h4>
              <div className="log-box">
                <p>AI validator output will appear here after processing.</p>
                <div className="muted small">Timestamp: {new Date().toLocaleString()}</div>
              </div>
            </div>

            {/* FOOTER */}
            <div className="form-footer">
              <div className="muted">Need help? Contact Target Support.</div>

              <div className="footer-actions">
                <button
                  className="btn btn-white small"
                  onClick={() => navigator.clipboard.writeText(window.location.href)}
                >
                  Copy Link
                </button>
                <button className="btn btn-white small" onClick={resetForm}>Reset</button>
              </div>
            </div>
          </section>
        )}
      </main>

      {/* FOOTER */}
      <footer className="site-footer">
        <div className="footer-inner">
          <div className="footer-brand">
            <div className="bull-small" aria-hidden>
              <svg viewBox="0 0 100 100" className="bull-small-svg">
                <circle cx="50" cy="50" r="50" fill="#fff" />
                <circle cx="50" cy="50" r="38" fill="#cc0000" />
                <circle cx="50" cy="50" r="22" fill="#fff" />
                <circle cx="50" cy="50" r="10" fill="#cc0000" />
              </svg>
            </div>
            <div>
              <div className="brand-name smallish">Target</div>
              <div className="muted">Customer support</div>
            </div>
          </div>

          <div className="muted"> {new Date().getFullYear()} Target Return UI</div>
        </div>
      </footer>
    </div>
  );
}
